import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { toast } from "react-toastify";
import { createCategory, editCategory, updateCategory } from "../../Redux/Actions/CategoryActions";
import { CATEGORY_CREATE_RESET, CATEGORY_UPDATE_RESET } from "../../Redux/Constants/CategoryConstants";
import Message from "../LoadingError/Error";
import Loading from "../LoadingError/Loading";



const Toastobjects = {
  pauseOnFocusLoss: false,
  draggable: false,
  pauseOnHover: false,
  autoClose: 2000,
};


const CreateCategory = (props) => {

  const categoryIdSelected = props.categoryIdSelected;

  const [isEnabled, setIsEnabled] = useState(0);
  const [title, setTitle] = useState("");
  const [categoryBanner, setCategoryBanner] = useState(null);
  const [categoryUrl, setCategoryUrl] = useState("");

  const dispatch = useDispatch();

  const categoryCreate = useSelector((state) => state.categoryCreate);
  const {loading, error, category} = categoryCreate;


  const categoryEdit = useSelector((state) => state.categoryEdit);
  const {loadingEdit, errorEdit, editCategory} = categoryEdit;

  const categoryUpdate = useSelector((state) => state.categoryUpdate);
  const { 
    loading: loadingUpdate, 
    error: errorUpdate, 
    success: successUpdate 
  } = categoryUpdate;

  useEffect( () => {
    if(category){
      toast.success("Category Added", Toastobjects);
      dispatch({type : CATEGORY_CREATE_RESET})
      setIsEnabled(0);
      setTitle("");
      setCategoryBanner(null);
      setCategoryUrl("");
    }

    if(successUpdate){
      dispatch({
        type: CATEGORY_UPDATE_RESET,
      });
      toast.success("Category Updated" , Toastobjects);
    }else{
      if(!categoryEdit.title || editCategory._id !== categoryIdSelected){
        dispatch(editCategory(categoryIdSelected));
      }else{
          setIsEnabled(editCategory.isEnabled);
          setTitle(editCategory.title);
          setCategoryBanner(editCategory.categoryBanner);
          setCategoryUrl(editCategory.categoryUrl);
      }
    }
  }, [category,editCategory, dispatch, categoryIdSelected, successUpdate])

  const submitHandler = (e) => {
    e.preventDefault();
    if(categoryIdSelected){
      dispatch(updateCategory({
        _id: categoryIdSelected,
        isEnabled,
        title,
        categoryBanner,
        categoryUrl,
      }))
    }else{
      dispatch(createCategory(isEnabled,title,categoryBanner,categoryUrl));
    }
  }

 

  return (
    <div className="col-md-12 col-lg-6">
      <form onSubmit={submitHandler}>
        {
          error && <Message variant="alert-danger">{error}</Message>
        }
        {
          loading && <Loading/>
        }
        {
          errorEdit && <Message variant="alert-danger">{errorEdit}</Message>
        }
        {
          loadingEdit && <Loading/>
        }
        <div className="mb-4">
          <label htmlFor="category_title" className="form-label">
            Category title
          </label>
          <input
            type="text"
            placeholder="Type here"
            className="form-control"
            id="category_title"
            required
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>

        <div className="d-grid">
          {
            categoryIdSelected ? 
            (
              <button className="btn btn-primary py-3">Update category</button>
            )
            :
            (
              <button className="btn btn-primary py-3">Create category</button>
            )
          }
        </div>
      </form>
    </div>
  );
};

export default CreateCategory;
