import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { toast } from "react-toastify";
import { createCategory, editCategory, listCategories, updateCategory } from "../../Redux/Actions/CategoryActions";
import { CATEGORY_UPDATE_RESET } from "../../Redux/Constants/CategoryConstants";
import Message from "../LoadingError/Error";
import Loading from "../LoadingError/Loading";
import { Link, useHistory } from "react-router-dom";


const Toastobjects = {
  pauseOnFocusLoss: false,
  draggable: false,
  pauseOnHover: false,
  autoClose: 2000,
};


const CreateCategory = (props) => {
  
  let history = useHistory();

  const categoryIdSelected = props.categoryIdSelected;

  const [isEnabled, setIsEnabled] = useState(0);
  const [title, setTitle] = useState("");
  const [categoryBanner, setCategoryBanner] = useState(null);
  const [categoryUrl, setCategoryUrl] = useState("");

  const dispatch = useDispatch();

  const categoryCreate = useSelector((state) => state.categoryCreate);
  const {loading, error, categoryCreator} = categoryCreate;

  const categoryEdit = useSelector((state) => state.categoryEdit);
  const {loading : loadingEdit, error: errorEdit, categoryRetriever} = categoryEdit;

  const categoryUpdate = useSelector((state) => state.categoryUpdate);
  const { 
    loading: loadingUpdate, 
    error: errorUpdate, 
    success: successUpdate 
  } = categoryUpdate;

  useEffect( () => {
    if(categoryCreator){
      toast.success("Category Added", Toastobjects);
    }
    if(successUpdate){
      toast.success("Category Updated" , Toastobjects);
    }
    if(categoryIdSelected){
      dispatch(editCategory(categoryIdSelected));
      history.push(`category/${categoryIdSelected}/edit`);
    }
    if(categoryEdit){
      console.log("44444444");
      console.log(categoryEdit);
    }
    /* console.log(categoryEdit);
    console.log(categoryRetriever);
    console.log(categoryCreate);
    console.log(categoryCreator);
    console.log(categoryUpdate); */
  /*   console.log(categoryCreator);
    console.log(successUpdate);
    console.log(categoryEdit);
    console.log(categoryRetriever);
    console.log(categoryIdSelected); */
    /* if(categoryIdSelected){
      if(!category){
        console.log("44444444444444444444444");
        dispatch(editCategory(categoryIdSelected));
      }else{
        console.log("33333333333333");
        console.log(categoryIdSelected);
      }
    } */
    /* setIsEnabled(categoryEdit.isEnabled);
      setTitle(categoryEdit.title);
      setCategoryBanner(categoryEdit.categoryBanner);
      setCategoryUrl(categoryEdit.categoryUrl);
     */
  }, [history, categoryCreator, dispatch, successUpdate, categoryIdSelected, categoryRetriever])

  const submitHandler = (e) => {
    e.preventDefault();
    if(categoryIdSelected || (categoryCreator && categoryCreator._id)){
      if(categoryCreator && categoryCreator._id){
        dispatch(updateCategory({_id: categoryCreator._id,isEnabled,title,categoryBanner,categoryUrl,}))
      }else{
        dispatch(updateCategory({_id: categoryIdSelected,isEnabled,title,categoryBanner,categoryUrl,}))
      }
    }else{
      dispatch(createCategory(isEnabled,title,categoryBanner,categoryUrl));
    }
  }

  return (
    <div className="col-md-12 col-lg-8">
      <form onSubmit={submitHandler}>
        {
          error && <Message variant="alert-danger">{error}</Message>
        }
        {
          loading && <Loading/>
        }
        <div className="mb-4">
          <label htmlFor="category_title" className="form-label">
            Category title
          </label>
          <input
            type="text"
            placeholder="Type here"
            className="form-control"
            id="category_title"
            required
            value={title || ''}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>

        <div className="d-grid">
          {
            categoryIdSelected || (categoryCreator && categoryCreator._id) ? 
            (
              <button className="btn btn-primary py-3">Update category</button>
            )
            :
            (
              <button className="btn btn-primary py-3">Create category</button>
            )
          }
        </div>
      </form>
    </div>
  );
};

export default CreateCategory;
