import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { toast } from "react-toastify";

import { createCategory } from "../../Redux/Actions/CategoryActions";
import { CATEGORY_CREATE_RESET } from "../../Redux/Constants/CategoryConstants";



const Toastobjects = {
  pauseOnFocusLoss: false,
  draggable: false,
  pauseOnHover: false,
  autoClose: 2000,
};


const CreateCategory = () => {

  const [isEnabled, setIsEnabled] = useState(0);
  const [title, setTitle] = useState("");
  const [categoryBanner, setCategoryBanner] = useState(null);
  const [children, setChildren] = useState(null);
  const [categoryUrl, setCategoryUrl] = useState("");

  const dispatch = useDispatch();

  const categoryCreate = useSelector((state) => state.categoryCreate);
  const {loading, error, category} = categoryCreate;

  useEffect( () => {
    if(category){
      toast.success("Category Added", Toastobjects);
      dispatch({type : CATEGORY_CREATE_RESET})
      setIsEnabled(0);
      setTitle("");
      setCategoryBanner(null);
      setChildren(null);
      setCategoryUrl("");
    }
  }, [category, dispatch])

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(createCategory(isEnabled,title,categoryBanner,children,categoryUrl));
  }

  return (
    <div className="col-md-12 col-lg-6">
      <form>
        <div className="mb-4">
          <label htmlFor="product_name" className="form-label">
            Name
          </label>
          <input
            type="text"
            placeholder="Type here"
            className="form-control py-3"
            id="product_name"
          />
        </div>
        <div className="mb-4">
          <label className="form-label">Images</label>
          <input className="form-control" type="file" />
        </div>
        <div className="mb-4">
          <label className="form-label">Description</label>
          <textarea
            placeholder="Type here"
            className="form-control"
            rows="4"
          ></textarea>
        </div>

        <div className="d-grid">
          <button className="btn btn-primary py-3">Create category</button>
        </div>
      </form>
    </div>
  );
};

export default CreateCategory;
